# sticking to rails and rspec-rails APIs to keep things simple and stable.

module ChannelStatstics
  RSpec.describe 'Notes', type: :request do
    let!(:server) { FactoryBot.create(:server, name: 'MyServer') }
    let!(:channel) { FactoryBot.create(:channel, server: server, name: 'MyChannel') }
    let(:channel_statistic) { FactoryBot.create(:channel_statistic, server: server,
                                                   channel: channel,
                                                   name: 'MyStatistic') }

    let!(:user) { FactoryBot.create(:user) }

    let(:valid_attributes) do
      {
        server_id: server.id,
        channel_id: channel.id,
        channel_statistic_id: channel_statistic.id,
        type: 'acknowledge',
        message: 'some special stuff',
        user_id: user.id
      }
    end

    let(:post_attributes) do
      {
        server_id: server.id,
        channel_id: channel.id,
        channel_statistic_id: channel_statistic.id,
        message: 'some special stuff'
      }
    end

    let(:invalid_attributes) do
      {
        type: 'very wrong type'
      }
    end

    before(:each) do
      login_admin
    end

    describe 'GET /index' do
      it 'renders a successful response' do
        Note.create! valid_attributes
        get channel_statistic_notes_url(channel_statistic)
        expect(response).to be_successful
      end
    end

    describe 'GET /show' do
      it 'renders a successful response' do
        note = Note.create! valid_attributes
        get channel_statistic_note_url(channel_statistic, note)
        expect(response).to be_successful
      end
    end

    describe 'GET /new' do
      it 'renders a successful response' do
        get new_channel_statistic_note_url(channel_statistic)
        expect(response).to be_successful
      end
    end

    describe 'GET /edit' do
      it 'render a successful response' do
        note = Note.create! valid_attributes
        get edit_channel_statistic_note_url(channel_statistic, note)
        expect(response).to be_successful
      end
    end

    describe 'POST /create' do
      context 'with valid parameters' do
        it 'creates a new Note' do
          expect do
            post channel_notes_url(channel), params: { note: post_attributes }
          end.to change(Note, :count).by(1)
        end

        it 'creates a new Note with type :note' do
          post channel_notes_url(channel), params: { note: post_attributes }
          expect(channel.notes.last.type).to eq('note')
        end

        it 'creates a new Note with type :acknowledge' do
          post channel_notes_url(channel), params: { note: post_attributes.merge(type: 'acknowledge') }
          expect(channel.notes.last.type).to eq('acknowledge')
        end

        it 'redirects to the created note' do
          post channel_notes_url(channel), params: { note: post_attributes }
          expect(response).to redirect_to(channel_url(channel, anchor: 'notes'))
        end
      end

      context 'with invalid parameters' do
        it 'does not create a new Note' do
          expect do
            post channel_notes_url(channel), params: { note: invalid_attributes }
          end.to change(Note, :count).by(0)
        end

        it "renders a successful response (i.e. to display the 'new' template)" do
          post channel_notes_url(channel), params: { note: invalid_attributes }
          expect(response).to have_http_status(422)
        end
      end
    end

    describe 'PATCH /update' do
      context 'with valid parameters' do
        let(:new_attributes) do
          {
            message: 'some other text'
          }
        end

        it 'updates the requested note' do
          note = Note.create! valid_attributes
          patch channel_statistic_note_url(channel_statistic, note), params: { note: new_attributes }
          note.reload
          expect(note.message.to_plain_text).to eq('some other text')
        end

        it 'redirects to the note' do
          note = Note.create! valid_attributes
          patch channel_statistic_note_url(channel_statistic, note), params: { note: new_attributes }
          note.reload
          expect(response).to redirect_to(channel_statistic_url(channel_statistic, anchor: 'notes'))
        end
      end

      context 'with invalid parameters' do
        it "renders a successful response (i.e. to display the 'edit' template)" do
          note = Note.create! valid_attributes
          patch channel_statistic_note_url(channel_statistic, note), params: { note: invalid_attributes }
          expect(response).to have_http_status(422)
        end
      end
    end

    describe 'DELETE /destroy' do
      it 'destroys the requested note' do
        note = Note.create! valid_attributes
        expect do
          delete channel_statistic_note_url(channel_statistic, note)
        end.to change(Note, :count).by(-1)
      end

      it 'redirects to the notes list' do
        note = Note.create! valid_attributes
        delete channel_statistic_note_url(channel_statistic, note)
        expect(response).to redirect_to(channel_statistic_url(channel_statistic, anchor: 'notes'))
      end
    end
  end
end
